import React, { useState, useEffect } from "react";
import "./TeaMug.css";

const TeaMug = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [skewDirection, setSkewDirection] = useState(0);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const newX = e.clientX;
      const direction = newX > position.x ? 1 : -1;

      if (direction !== skewDirection) {
        setSkewDirection(direction);
      }

      setPosition({ x: newX, y: e.clientY });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, [position, skewDirection]);

  const vaporStyles = [
    { "--i": 1 },
    { "--i": 3 },
    { "--i": 16 },
    { "--i": 5 },
    { "--i": 13 },
    { "--i": 20 },
    { "--i": 6 },
    { "--i": 7 },
    { "--i": 10 },
    { "--i": 8 },
    { "--i": 17 },
    { "--i": 11 },
    { "--i": 12 },
    { "--i": 14 },
    { "--i": 2 },
    { "--i": 9 },
    { "--i": 15 },
    { "--i": 4 },
    { "--i": 19 },
  ];

  return (
    <div className="container" style={{ top: position.y, left: position.x }}>
      <div className="plate"></div>
      <div className="cup">
        <div className="top">
          <div className="vapour">
            {vaporStyles.map((style, index) => (
              <span key={index} style={style}></span>
            ))}
          </div>
          <div className="circle">
            <div
              className="tea"
              style={{ transform: `skewY(${skewDirection * 10}deg)` }}
            ></div>
          </div>
        </div>
      </div>
      <div className="handle"></div>
    </div>
  );
};

export default TeaMug;
