import React, { useState } from "react";
import { useSpring, animated } from "react-spring";

const CoffeeMug = () => {
  const [isClicked, setIsClicked] = useState(false);

  const [{ x, y, spill, smoke }, set] = useSpring(() => ({
    x: 0,
    y: 0,
    spill: 0,
    smoke: 0,
  }));

  const handleMouseMove = (e) => {
    set({ x: e.clientX - 50, y: e.clientY - 50 });
  };

  const handleMouseClick = () => {
    setIsClicked(true);
    set({ spill: 1 });
  };

  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      style={{ position: "relative", height: "100vh" }}
    >
      <animated.div
        style={{
          transform: x.to((val) => `translateX(${val}px)`),
          transition: "transform 0.5s",
        }}
      >
        <animated.div
          style={{
            transform: y.to((val) => `translateY(${val}px)`),
            transition: "transform 0.5s",
          }}
        >
          <animated.div
            style={{
              position: "absolute",
              bottom: 0,
              left: "50%",
              transform: "translateX(-50%)",
            }}
          >
            <animated.div
              style={{
                height: 150,
                width: 100,
                background: "brown",
                borderRadius: 10,
                overflow: "hidden",
                position: "relative",
              }}
            >
              <animated.div
                style={{
                  position: "absolute",
                  bottom: -10,
                  left: "50%",
                  transform: "translateX(-50%)",
                  height: spill.to((val) => val * 100 + "%"),
                  width: "80%",
                  background: "brown",
                }}
              />
              <animated.div
                style={{
                  position: "absolute",
                  top: -10,
                  left: "50%",
                  transform: "translateX(-50%)",
                  height: smoke.to((val) => val * 100 + "%"),
                  width: "20%",
                  background: "rgba(255, 255, 255, 0.3)",
                  borderRadius: "50%",
                }}
              />
            </animated.div>
          </animated.div>
        </animated.div>
      </animated.div>
    </div>
  );
};

export default CoffeeMug;
