// CoffeeMug.js
import React, { useState, useEffect } from "react";
import "./CoffeeMug.css";

const CoffeeMug = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isSpilled, setIsSpilled] = useState(false);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });
      setIsSpilled(false);
    };

    const handleMouseStop = () => {
      setIsSpilled(true);
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mouseup", handleMouseStop);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseup", handleMouseStop);
    };
  }, []);

  const mugStyles = {
    left: cursorPosition.x,
    top: cursorPosition.y,
  };

  return (
    <div className={`mug ${isSpilled ? "spilled" : ""}`} style={mugStyles}>
      <div className="coffee"></div>
      <div className="smoke"></div>
    </div>
  );
};

export default CoffeeMug;
