import React, { useState, useEffect } from "react";
import "./TeaMug.css";

const TeaMug = () => {
  const [skewDirection, setSkewDirection] = useState(0); // 0 for no skew, -1 for left, 1 for right
  let lastMouseX = 0;
  let moveTimer;

  useEffect(() => {
    const handleMouseMove = (e) => {
      const newX = e.clientX;
      const direction = newX > lastMouseX ? 1 : -1;

      if (direction !== skewDirection) {
        setSkewDirection(direction);
      }

      lastMouseX = newX;

      // Clear the timer and set a new one for detecting rapid movements
      clearTimeout(moveTimer);
      moveTimer = setTimeout(() => {
        setSkewDirection(0); // Reset skew when cursor stops or moves slowly
      }, 100); // Adjust the timeout duration as needed
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, [skewDirection]);

  const vaporStyles = [
    { "--i": 1 },
    { "--i": 3 },
    { "--i": 16 },
    { "--i": 5 },
    { "--i": 13 },
    { "--i": 20 },
    { "--i": 6 },
    { "--i": 7 },
    { "--i": 10 },
    { "--i": 8 },
    { "--i": 17 },
    { "--i": 11 },
    { "--i": 12 },
    { "--i": 14 },
    { "--i": 2 },
    { "--i": 9 },
    { "--i": 15 },
    { "--i": 4 },
    { "--i": 19 },
  ];

  return (
    <div className="container">
      <div className="plate"></div>
      <div className="cup">
        <div className="top">
          <div className="vapour">
            {vaporStyles.map((style, index) => (
              <span key={index} style={style}></span>
            ))}
          </div>
          <div className="circle">
            <div
              className="tea"
              style={{
                transform: `skewY(${
                  skewDirection === 1 ? 10 : skewDirection === -1 ? -10 : 0
                }deg)`,
                transition: "transform 0.2s ease-in-out",
              }}
            ></div>
          </div>
        </div>
      </div>
      <div className="handle"></div>
    </div>
  );
};

export default TeaMug;
