// CoffeeMug.js
import React, { useState, useEffect } from "react";
import "./CoffeeMug.css";

const CoffeeMug = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isSpilled, setIsSpilled] = useState(false);

  useEffect(() => {
    let lastCursorPosition = { x: 0, y: 0 };

    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.clientX, y: e.clientY });

      const isFastMovement =
        Math.abs(e.movementX) > 5 || Math.abs(e.movementY) > 5;

      setIsSpilled(isFastMovement);
      lastCursorPosition = { x: e.clientX, y: e.clientY };
    };

    const handleMouseStop = () => {
      // Calculate the distance moved since the last mousemove event
      const distanceMoved = Math.sqrt(
        Math.pow(cursorPosition.x - lastCursorPosition.x, 2) +
          Math.pow(cursorPosition.y - lastCursorPosition.y, 2)
      );

      // Check if the distance moved is below a certain threshold
      const isSuddenStop = distanceMoved < 5; // Adjust the threshold as needed

      setIsSpilled(isSuddenStop);
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mouseup", handleMouseStop);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseup", handleMouseStop);
    };
  }, [cursorPosition]);

  const mugStyles = {
    left: cursorPosition.x,
    top: cursorPosition.y,
  };

  return (
    <div className={`mug ${isSpilled ? "spilled" : ""}`} style={mugStyles}>
      <div className="coffee"></div>
      <div className="smoke"></div>
    </div>
  );
};

export default CoffeeMug;
