// FlyingSnowman.js
import React, { useState, useEffect } from "react";
import "./FlyingSnowman.css";

const FlyingSnowman = ({ mouseX, mouseY }) => {
  const [expression, setExpression] = useState("smile");

  useEffect(() => {
    const distance = Math.sqrt(
      Math.pow(mouseX - window.innerWidth / 2, 2) +
        Math.pow(mouseY - window.innerHeight + 50, 2)
    );

    if (distance < 30) {
      setExpression("smile");
    } else {
      setExpression("shock");
    }
  }, [mouseX, mouseY]);

  const snowmanStyle = {
    left: mouseX + "px",
    bottom: window.innerHeight - mouseY + "px",
  };

  return (
    <div className={`flying-snowman ${expression}`} style={snowmanStyle}>
      <div className="eye"></div>
      <div className="eye" style={{ left: "25px" }}></div>
      {expression === "smile" && <div className="mouth"></div>}
    </div>
  );
};

export default FlyingSnowman;
